import { useContext } from "react";

import { view } from "../../pages/Q1";
import { setView } from "../../pages/Q1";

const ContextQ1Detail2 = () => {
    const view2 = useContext(view);
    const setView2 = useContext(setView);

    const onShow2 = () => {
        switch (view2) {
            case "false":
                return setView2("one");
            case "one":
                return setView2("two");
        }
        console.log(view2);
    };

    return (
        <>
            <h2>ContextQ1Detail2</h2>

            <button onClick={onShow2}>
                {view2 === "two" ? "숨기기" : "보이기"}
            </button>
        </>
    );
};
export default ContextQ1Detail2;
