import { useContext } from "react";
import ContextQ1Detail2 from "./Detail2";

import { view } from "../../pages/Q1";
import { setView } from "../../pages/Q1";

const ContextQ1Detail = () => {
    const view1 = useContext(view);
    const setView1 = useContext(setView);

    //보여주기
    const onShow1 = () => {
        if (view1 === false) {
            setView1("one");
        } else if (view1 === "one") {
            setView1("two");
        }
    };

    //숨기기
    const onhide1 = () => {
        if (view1 === "two") {
            setView1("one");
        } else if (view1 === "one") {
            setView1("false");
        }
    };

    return (
        <>
            <h2>ContextQ1Detail</h2>

            {view1 === "two" ? (
                <button onClick={onhide1}>숨기기</button>
            ) : (
                <button onClick={onShow1}>보이기</button>
            )}
            <ContextQ1Detail2 />
        </>
    );
};
export default ContextQ1Detail;
