import styled from "styled-components";

function Comment({ post, setPost }) {
    const onClickDel = () => {
        setPost(post.filter((list) => list.User.nickname !== post));
    };
    return (
        <S.CommentItem>
            {post.map((posts) => (
                <>
                    <p>
                        작성자: <span>{posts.User.nickname}</span>
                    </p>
                    <p>
                        댓글 내용: <span>{posts.content}</span>
                    </p>
                    <button>수정</button>
                    <button onClick={onClickDel}>삭제</button>
                </>
            ))}
        </S.CommentItem>
    );
}
export default Comment;

const CommentItem = styled.li`
    border: 1px solid #000;
    margin: 10px;
`;

const S = {
    CommentItem,
};
