import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import productList from "../__mock__/products.json";

function DetailPage() {
    const navigate = useNavigate();
    const [newarr, setNewarr] = useState("");
    const [inputs, setInputs] = useState({
        name: "",
        review: "",
        rating: "",
    });

    const { name, review, rating } = inputs;

    //인풋 3개 입력값
    const onPushInput = (el) => {
        const { value, name } = el.target;
        console.log(newarr.review);
    };

    //pushBtn
    const onAddReview = () => {
        console.log("dd");
    };

    useEffect(() => {
        setNewarr(
            productList.products.filter(
                (el) => el.productNumber === productNumber
            )
        );
        console.log(newarr.productNumber, params.productNumber);
        if (newarr.productNumber === params.productNumber) navigate("/");
    }, []);

    const [data, setData] = useState(productList.products);
    const params = useParams();

    const { productNumber } = params;

    return (
        <>
            <div>
                <h4>{data[0].productName}</h4>
                <p>상품번호: {data[0].productNumber}</p>
                <p>가격: {Number(data[0].productPrice).toLocaleString()}원</p>
                <p>사이즈: {data[0].productSize}</p>
                <p>평점: {data[0].productRating}</p>
                <p>리뷰: {data[0].productReview}</p>
                <hr />
                <h2>리뷰</h2>
                <hr />
                {data[0].Review.map((list) => (
                    <>
                        <p>reviewer: {list.reviewer}</p>
                        <p>review: {list.review}</p>
                        <sp>rating: {list.rating}</sp>
                        <hr />
                    </>
                ))}
                <input
                    placeholder="reviewer"
                    name="name"
                    onChange={onPushInput}
                />
                <input
                    placeholder="review"
                    name="review"
                    onChange={onPushInput}
                />
                <input
                    placeholder="rating"
                    name="rating"
                    onChange={onPushInput}
                />
                <button onClick={onAddReview}>리뷰 추가</button>
            </div>
        </>
        /* 상세 페이지는 자유롭게 꾸미시면 됩니다. 아직 해당 부분의 진도가
            나가지 않았기 때문에 주소의 파람을 가지고 올 수 있는 방법은 미리
            콘솔에 찍어두었습니다. 단, 없는 번호 상품으로 접근 시 state페이지로
            돌아가도록 구현해주세요 */

