import { useRef, useState } from "react";
import PlayListMock from "../../__mock__/playList.json";

function State1() {
    // input state
    const [inputs, setInputs] = useState({
        title: "",
        signer: "",
    });

    const [list, setList] = useState(PlayListMock.playlist);
    const [render, setRender] = useState(false);

    const onChangeinput = (e) => {
        const [title, signer] = e.target;
        setTitle(e.target.value);
    };

    const onClickAdd = () => {
        list.push({ title: Title, signer: Singer });
        setRender((prev) => !prev);
    };
    const onClickRemove = (targetDel) => {
        setList(list.filter((list) => list.title !== targetDel));
    };

    return (
        <>
            <h1>문제1</h1>
            <ul>
                {list.map((playlist) => (
                    <li>
                        <h3>{playlist.title}</h3>
                        <p>{playlist.signer}</p>
                        <button onClick={() => onClickRemove(playlist.title)}>
                            삭제
                        </button>
                        <hr />
                    </li>
                ))}
            </ul>
            <div>
                <p>
                    곡명 : <input onChange={onChangeinput} value={title} />
                </p>
                <p>
                    가수/작곡 :{" "}
                    <input onChange={onChangeinput} value={signer} />
                </p>
                <p>
                    <button onClick={onClickAdd}>추가</button>
                </p>
            </div>
        </>
    );
}
export default State1;
