import { useRef, useState } from "react";
import PlayListMock from "../../__mock__/playList.json";

function State1() {
    const [Title, setTitle] = useState();
    const [Singer, setSinger] = useState();
    const [render, setRender] = useState(false);

    const onChangeTitle = (title) => {
        setTitle(title.target.value);
    };
    const onChangeSinger = (singer) => {
        setSinger(singer.target.value);
    };

    const onClickAdd = () => {
        setRender((prev) => !prev);
        PlayListMock.playlist.push({ title: Title, signer: Singer });
    };

    const onClickRemove = () => {
        PlayListMock.playlist.filter((el) => el.title !== Title);
    };

    return (
        <>
            <h1>문제1</h1>
            <ul>
                {PlayListMock.playlist.map((playlist) => (
                    <li>
                        <h3>{playlist.title}</h3>
                        <p>{playlist.signer}</p>
                        <button onClick={onClickRemove}>삭제</button>
                        <hr />
                    </li>
                ))}
            </ul>
            <div>
                <p>
                    곡명 : <input onChange={onChangeTitle} />
                </p>
                <p>
                    가수/작곡 : <input onChange={onChangeSinger} />
                </p>
                <p>
                    <button onClick={onClickAdd}>추가</button>
                </p>
            </div>
        </>
    );
}
export default State1;
