    // if (newarr.productNumber === productNumber) return navigate("/");

    const [data, setData] = useState(productList.products);

    return (
        <>
            <h4>{newarr.productName}</h4>
            <p>상품번호: {newarr.productNumber}</p>
            <p>가격: {Number(newarr.productPrice).toLocaleString()}원</p>
            <p>사이즈: {newarr.productSize}</p>
            <p>평점: {newarr.productRating}</p>
            <p>리뷰: {newarr.productReview}</p>
            <hr />
            <h2>리뷰</h2>
            <hr />
            {newarr.Review.map((el) => (
                <>
                    <div>reviewer :{el.reviewer}</div>
                    <div>review :{el.review}</div>
                    <div>rating :{el.rating}</div>
                </>
            ))}
        </>
    );

