import { useState } from "react";
import styled from "styled-components";

function Comment({ post, setPost }) {
    //삭제
    const deleteBtn = (el) => {
        window.confirm("정말 삭제 하시겠습니까?");
        {
            if (el.myComment === false) return alert("XXX 삭제 불가 XXX");
            setPost((prev) => ({
                ...prev,
                Comments: [
                    ...prev.Comments.filter(
                        (e) => e.User.nickname !== el.User.nickname
                    ),
                ],
            }));
        }
    };
    const [changeView, setChangeView] = useState(false);
    //수정
    const ChangeBtn = (el) => {
        console.log(el.change);
        setPost((post) => ({ ...post, ...(post.Comments.change = true) }));
    };
    return (
        <S.CommentItem>
            {post.map((el) => (
                <>
                    <p>
                        작성자: <span>{el.User.nickname}</span>
                    </p>
                    {el.change ? (
                        <input placeholder="수정할 댓글 내용" />
                    ) : (
                        <p>
                            댓글 내용: <span>{el.content}</span>
                        </p>
                    )}

                    <button onClick={() => deleteBtn(el)}>삭제</button>
                    <button onClick={() => ChangeBtn(el)}>수정</button>
                </>
            ))}
        </S.CommentItem>
    );
}
export default Comment;

const CommentItem = styled.li`
    border: 1px solid #000;
    margin: 10px;
`;

const S = {
    CommentItem,
};
