import styled from "styled-components";

function Comment({ post }) {
    return (
        <S.CommentItem>
            <p>
                작성자: <span>{post.User.nickname}</span>
            </p>
            <p>
                댓글 내용: <span></span>
            </p>
            <button>수정</button>
            <button>삭제</button>
        </S.CommentItem>
    );
}
export default Comment;

const CommentItem = styled.li`
    border: 1px solid #000;
    margin: 10px;
`;

const S = {
    CommentItem,
};
