import styled from "styled-components";

function Comment({ post, setPost }) {
    const onClickDel = (nick) => {
        setPost(post.filter((el) => el.User.nickname !== nick));
    };
    return (
        <S.CommentItem>
            {post.map((posts) => (
                <>
                    <p>
                        작성자: <span></span>
                    </p>
                    <p>
                        댓글 내용: <span></span>
                    </p>
                    <button>수정</button>
                    <button>삭제</button>
                </>
            ))}
        </S.CommentItem>
    );
}
export default Comment;

const CommentItem = styled.li`
    border: 1px solid #000;
    margin: 10px;
`;

const S = {
    CommentItem,
};
