import { useParams } from "react-router-dom";
import productList from "../__mock__/products.json";

function DetailPage() {
    const [data, setData] = useState(productList.products);
    const params = useParams();
    const { productNumber } = params;

    let newarr = productList.products.filter(
        (el) => el.productNumber === productNumber
    );

    console.log(newarr);

    return (
        <>
            <div>
                <h4>{newarr[0].list.productName}</h4>
                <p>상품번호: {newarr[0].list.productNumber}</p>
                <p>
                    가격: {Number(newarr[0].list.productPrice).toLocaleString()}
                    원
                </p>
                <p>사이즈: {newarr[0].list.productSize}</p>
                <p>평점: {newarr[0].list.productRating}</p>
                <p>리뷰: {newarr[0].list.productReview}</p>
            </div>
            ;
        </>
