import { useRef, useState } from "react";
import PlayListMock from "../../__mock__/playList.json";

function State1() {
    /* 
    문제 1.

    state를 다루기 위한 기초 문제입니다.
    음악 목록 10가지의 mock data가 있습니다.

    아래에 추가버튼을 눌러 목록에 리스트를 추가하고 
    삭제 버턴을 눌렀을 때 데이터가 삭제될 수 있도록 해주세요
  */
    /* 데이터 콘솔에 찍어두었으니 확인해볼 것 */
    const [view, setView] = useState(false);
    const [inputValue, setInputValue] = useState("");
    const [inputSinger, setInputSinger] = useState("");

    const [list, setList] = useState(PlayListMock.playlist);
    console.log(list);
    //추가
    const onClickList = () => {
        PlayListMock.playlist.push({ title: inputValue, signer: inputSinger });
        setView((prev) => !prev);
    };

    const onClickRemove = () => {};
    const onChangeInput = (el) => {
        setInputValue(el.target.value);
    };
    const onChangeSinger = (el) => {
        setInputSinger(el.target.value);
    };
    return (
        <>
            <h1>문제1</h1>
            <ul>
                {PlayListMock.playlist.map((el) => (
                    <li>
                        <h3>{el.title}</h3>
                        <p>{el.signer}</p>
                        <button onClick={onClickRemove}>삭제</button>
                        <hr />
                    </li>
                ))}
            </ul>
            <div>
                <p>
                    곡명 : <input onChange={onChangeInput} />
                </p>
                <p>
                    가수/작곡 : <input onChange={onChangeSinger} />
                </p>
                <p>
                    <button onClick={onClickList}>추가</button>
                </p>
            </div>
        </>
    );
}
export default State1;
