import PlayListMock from "../../__mock__/playList.json";

function State1() {
    return (
        <>
            <h1>문제1</h1>
            <ul>
                {list.map((el) => (
                    <li>
                        <h3>{el.title}</h3>
                        <p>{el.signer}</p>
                        <button onClick={onClickRemove}>삭제</button>
                        <hr />
                    </li>
                ))}
            </ul>
            <div>
                <p>
                    곡명 : <input onChange={onChangeInput} />
                </p>
                <p>
                    가수/작곡 : <input onChange={onChangeSinger} />
                </p>
                <p>
                    <button onClick={onClickList}>추가</button>
                </p>
            </div>
        </>
    );
}
export default State1;
