import { useRef, useState } from "react";
import PlayListMock from "../../__mock__/playList.json";

function State1() {
    const [useplayList, setPlayList] = useState(PlayListMock.playlist);
    /*데이터 콘솔에 찍어두었으니 확인해볼 것 */
    const onClickRemove = () => {
        console.log(useplayList);
    };
    const onClickAdd = () => {
        setPlayList(
            PlayListMock.playlist.push({
                title: titleValue.current.value,
                signer: singerValue.current.value,
            })
        );
        console.log(useplayList);
    };

    return (
        <>
            <h1>문제1</h1>
            <ul>
                {PlayListMock.playlist.map((playlist) => (
                    <li>
                        <h3>{playlist.title}</h3>
                        <p>{playlist.signer}</p>
                        <button onClick={onClickRemove}>삭제</button>
                        <hr />
                    </li>
                ))}
            </ul>
            <div>
                <p>
                    곡명 : <input onChange={onChangeTitle} />
                </p>
                <p>
                    가수/작곡 : <input ref={singerValue} />
                </p>
                <p>
                    <button onClick={onClickAdd} titleValue={titleValue}>
                        추가
                    </button>
                </p>
            </div>
        </>
    );
}
export default State1;
