import PlayListMock from "../../__mock__/playList.json";

function State1() {
    /* 
    문제 1.

    state를 다루기 위한 기초 문제입니다.
    음악 목록 10가지의 mock data가 있습니다.

    아래에 추가버튼을 눌러 목록에 리스트를 추가하고 
    삭제 버턴을 눌렀을 때 데이터가 삭제될 수 있도록 해주세요
  */

    /* 데이터 콘솔에 찍어두었으니 확인해볼 것 */
    const onChangeTitle = (title) => {
        console.log(title.target.value);
    };
    const onChangeSinger = (singer) => {
        console.log(singer.target.value);
    };
    const onClickAdd = () => {
        console.log(onChangeTitle);
    };

    return (
        <>
            <h1>문제1</h1>
            <ul>
                {PlayListMock.playlist.map((el) => (
                    <li>
                        <h3>{el.title}</h3>
                        <p>{el.signer}</p>
                        <hr />
                    </li>
                ))}
                {/* list */}
                {/* 예시 데이터입니다 */}
                {/* <li>
                    <h3>Summer</h3>
                    <p>Joe Hisaishi</p>
                </li> */}
            </ul>
            <div>
                <p>
                    곡명 : <input onChange={onChangeTitle} />
                </p>
                <p>
                    가수/작곡 : <input onChange={onChangeSinger} />
                </p>
                <p>
                    <button onClick={onClickAdd}>추가</button>
                    <button>삭제</button>
                </p>
            </div>
        </>
    );
}
export default State1;
