import { useState } from "react";

import PlayListMock from "../../__mock__/playList.json";

const [list, setList] = useState(PlayListMock);

function State1() {
    return (
        <>
            <h1>문제1</h1>
            <ul>
                {PlayListMock.playlist.map((el) => (
                    <li>
                        <h3>{el.title}</h3>
                        <p>{el.signer}</p>
                        <button>삭제</button>
                        <hr />
                    </li>
                ))}
            </ul>
            <div>
                <p>
                    곡명 : <input />
                </p>
                <p>
                    가수/작곡 : <input />
                </p>
                <p>
                    <button>추가</button>
                </p>
            </div>
        </>
    );
}
export default State1;
