import { useState } from "react";
import styled, { createGlobalStyle } from "styled-components";

function ProductCard({ onNavigate }) {
    const [price, setPrice] = useState("3000");
    const taek = () => {
        let priceSplit = price.split("");
        priceSplit.splice(priceSplit.length, 4, ",");
        console.log(priceSplit);
    };
    // 3 0 0 0  길이 4
    return (
        <>
            <button onClick={taek}>price</button>
            <S.Item onClick={onNavigate}>
                <h4>구멍난 양말</h4>
                <p>상품번호: 302012</p>
                <p>가격: {price}원</p>
                <p>사이즈: X, M, L</p>
                <p>평점: 4.5</p>
                <p>리뷰: 14</p>
            </S.Item>
        </>
    );
}

export default ProductCard;

const Item = styled.li`
    border: 1px solid #000;
    cursor: pointer;
    width: 300px;
    margin: 16px auto;
`;

const S = {
    Item,
};
